AutoForm.ClientEvent:
if (arg.keyCode === 13) {
window.clearTimeout(self.urlQueryTimeout);
self.urlQueryTimeout = window.setTimeout(function() {
var data = view.get("#formCondition.entity");
with (view.get("#dsEvent")) {
set("parameter", data);
flushAsync();
}
}, 250);
}

ToolBarButton.ClientEvent:
var data=view.get("#dsEvent.data");
if(data.current){
view.get("#dsEventRecord").flushAsync(); 
view.get("#dsAttachmentFile").flushAsync(); 

view.get("#dialogEvent").show();
}else{
dorado.MessageBox.alert("当前表没有信息可供编辑!");
}

DataGrid.ClientEvent:
var OperationCellRendererDetail = $extend(dorado.widget.grid.SubControlCellRenderer, {
    createSubControl: function(arg) {
        var entity = arg.data;
        var container = new dorado.widget.Container({
            layout: new dorado.widget.layout.HBoxLayout()
        });
        container.set("children", [new dorado.widget.Button({
            iconClass: "fa fa-eye",
			tip:"详情",
            onClick: function(self) {
				var data=view.get("#dsEvent.data"); 
				view.get("#dsImages").flushAsync();
				view.get("#dsVideos").flushAsync();
				view.get("#dsEventRecord").flushAsync();
				view.get("#dialogEvent").show(); 
            }
        }),new dorado.widget.Button({
            icon: "/static/svg/num/integralPlus.svg",
			tip:"有积分",
            onClick: function(self) {
                entity.set("integral", true);
				view.get("#updateAction").execute();
            }
        }),new dorado.widget.Button({
            icon: "/static/svg/num/integralMinus.svg",
			tip:"无积分",
            onClick: function(self) {
                entity.set("integral", false);
				view.get("#updateAction").execute();
            }
        })]);
        return container;
    },

    refreshSubControl: function(container, arg) {
        var children = container.get("children");
    }
});

self.set("#detail.renderer", new OperationCellRendererDetail());

DataColumn.ClientEvent:
$(arg.dom).empty().xCreate(
			{
				tagName : "IMG",
				src : arg.data.get("user.avatar"),
				//style : "margin: 2px"
				style : "width: 30px; height: 30px; margin: 1px"
			});

	dorado.TipManager.initTip(arg.dom.parentNode, {
		content : {
			tagName : "IMG",
			src : arg.data.get("user.avatar"),
			style : "width: 120px; height: 120px; margin: 2px"
		},
		arrowDirection : "top",
		arrowAlign : "left"
	});

