DataGrid.ClientEvent:
var OperationCellRendererDetail = $extend(dorado.widget.grid.SubControlCellRenderer, {
    createSubControl: function(arg) {
        var entity = arg.data;
        var container = new dorado.widget.Container({
            layout: new dorado.widget.layout.HBoxLayout()
        });
        container.set("children", [new dorado.widget.Button({
            iconClass: "icon-user-add",
			caption:"选择",
			ui:"highlight",
            onClick: function(self) {
				var entityEvent = view.get("#dsEvent").getData("#");
				var eventId = entityEvent.get("id");
				var userId=entity.get("username");
				var parameter = {
					"eventId": eventId,
					"userId":userId
				}
				view.get("#aaSave2").set("parameter", parameter).execute(function() {
					view.get("#dialogSelectUser").hide();
					view.get("#dsEventRecord").flushAsync();
				})
            }
        })]);
        return container;
    },

    refreshSubControl: function(container, arg) {
        var children = container.get("children");
    }
});

self.set("#detail.renderer", new OperationCellRendererDetail());

